mapper.select:
SELECT A.BORD_NO AS bordNo , A.DEL_YN AS delYn , A.BORD_TYPE AS bordType , A.BORD_KEY AS bordKey , (SELECT APT_NAME FROM apt000t WHERE APT_NO = A.BORD_NO ) bordName , A.BORD_TITLE AS bordTitle , A.BORD_SUB_TITLE AS bordSubTitle , A.BORD_CONTENT AS bordContent , A.BORD_RD_CNT AS bordRdCnt , A.BORD_GD_CNT AS bordGdCnt , A.APT_NO AS aptNo , (SELECT S.APT_NAME FROM IBJ_APT000T S WHERE S.APT_NO = A.APT_NO) aptNm , A.SLLR_NO AS sllrNo , A.REQ_NO AS reqNo , A.CTGR_ID AS ctgrId , (SELECT S.CTGR_NM FROM IBJ_CTGR000T S WHERE S.CTGR_ID = A.CTGR_ID) ctgrNm , (SELECT S.STSF_RATE FROM request110t S WHERE S.REQ_NO = A.REQ_NO AND S.COMPANY_ID = A.SLLR_NO) AS stsfRate , (SELECT COUNT(S.CMMT_NO) FROM IBJ_BORD110T S WHERE S.BORD_NO = A.BORD_NO AND S.DEL_YN = 'N') AS commentCnt , (SELECT S.CMMT_CONTENT FROM IBJ_BORD110T S WHERE S.BORD_NO = A.BORD_NO AND S.DEL_YN = 'N' LIMIT 1) AS cmmtContent , (SELECT (SELECT S2.NICK_NAME FROM IBJ_PRSN000T S2 WHERE S2.CLERK_NO = S.CRE_USER) FROM IBJ_BORD110T S WHERE S.BORD_NO = A.BORD_NO AND S.DEL_YN = 'N' LIMIT 1) AS cmmtUserNm , (SELECT DATE_FORMAT(S.CRE_DATE, '%y.%m.%d') FROM IBJ_BORD110T S WHERE S.BORD_NO = A.BORD_NO AND S.DEL_YN = 'N' LIMIT 1) AS cmmtcreDateTxt , A.CRE_USER AS creUser , (SELECT S.NICK_NAME FROM IBJ_PRSN000T S WHERE S.CLERK_NO = A.CRE_USER) AS creUserNm , (SELECT S.IMG_PATH FROM IBJ_PRSN000T S WHERE S.CLERK_NO = A.CRE_USER) AS profileImg , A.CRE_DATE AS creDate , CASE WHEN TIMESTAMPDIFF(MINUTE, A.CRE_DATE, NOW()) < 5 THEN '방금 전' WHEN TIMESTAMPDIFF(HOUR, A.CRE_DATE, NOW()) < 1 THEN CONCAT(TIMESTAMPDIFF(MINUTE, A.CRE_DATE, NOW()), '분 전') WHEN TIMESTAMPDIFF(HOUR, A.CRE_DATE, NOW()) < 4 THEN CONCAT(TIMESTAMPDIFF(HOUR, A.CRE_DATE, NOW()), '시간 ', MOD(TIMESTAMPDIFF(MINUTE, A.CRE_DATE, NOW()), 60), '분 전') WHEN DATE(A.CRE_DATE) = CURDATE() THEN DATE_FORMAT(A.CRE_DATE, '%H:%i') ELSE DATE_FORMAT(A.CRE_DATE, '%y.%m.%d') END AS creDateTxt , A.UPD_USER AS updUser , A.UPD_DATE AS updDate , (SELECT GROUP_CONCAT(S.FILE_LOC, S.FILE_ID) FROM wit_file000t S WHERE S.BIZ_KEY = A.BORD_NO) AS imagePath FROM IBJ_BORD100T A LEFT OUTER JOIN IBJ_BORD120T B ON B.BORD_NO = A.BORD_NO WHERE A.DEL_YN = 'N' AND A.BORD_TYPE = #{bordType} AND IFNULL((SELECT MAX(S.REPORT_STAT) FROM IBJ_BORD120T S WHERE S.BORD_NO = A.BORD_NO), '') NOT IN ('20', '30')
AND A.BORD_KEY = #{bordKey}
AND A.APT_NO = #{aptNo}
AND A.SLLR_NO = #{sllrNo}
AND A.REQ_NO = #{reqNo}
AND A.CTGR_ID = #{ctgrId}
AND A.CRE_USER = #{creUserId}
AND A.BORD_TITLE LIKE CONCAT('%', #{searchText}, '%')
ORDER BY A.CRE_DATE DESC LIMIT #{pageSize} OFFSET #{currentPage}

SELECT 
	CONCAT(S.FILE_LOC, S.FILE_ID) AS imagePath
FROM wit_file000t S
WHERE S.BIZ_CD = #{bordType}
AND S.BIZ_KEY = #{bordNo}

mapper.update:
UPDATE IBJ_BORD100T SET
	  BORD_TITLE = #{bordTitle}
	, BORD_CONTENT = #{bordContent}
	, UPD_USER = #{updUser}
	, UPD_DATE = NOW()
WHERE BORD_NO = #{bordNo}

UPDATE IBJ_BORD100T SET
	  DEL_YN = 'Y'
	, UPD_USER = #{updUser}
	, UPD_DATE = NOW()
WHERE DEL_YN = 'N'
AND BORD_NO = #{bordNo}

UPDATE IBJ_BORD110T SET
	  DEL_YN = 'Y'
	, UPD_USER = #{updUser}
	, UPD_DATE = NOW()
WHERE DEL_YN = 'N'
AND BORD_NO = #{bordNo}
AND CMMT_NO = #{cmmtNo}

UPDATE IBJ_BORD110T SET
	  DEL_YN = 'Y'
	, UPD_USER = #{updUser}
	, UPD_DATE = NOW()
WHERE DEL_YN = 'N'
AND BORD_NO = #{bordNo}

mapper.insert:
INSERT INTO wit_file000t
(
	  BIZ_CD
	, BIZ_KEY
	, FILE_SEQ
	, FILE_ID
	, FILE_NM
	, FILE_LOC
	, FILE_SIZE
	, CRE_USER
	, CRE_DATE
) 
VALUES
(
	  #{bizCd}
	, #{bizKey}
	, (SELECT IFNULL(MAX(CAST(S.FILE_SEQ AS UNSIGNED)), 0) + 1 FROM wit_file000t S
		WHERE S.BIZ_CD = #{bizCd}
		AND S.BIZ_KEY = #{bizKey})
	, #{fileId}
	, #{fileNm}
	, #{fileLoc}
	, #{fileSize}
	, #{creUser}
	, NOW()
)

mapper.select:
SELECT 
	COUNT(BORD_NO) AS CNT
FROM IBJ_BORD120T
WHERE BORD_NO = #{bordNo}
AND REPORT_STAT = '10'
AND CRE_USER = #{creUser}

mapper.update:
UPDATE IBJ_BORD120T SET
	REPORT_STAT = #{reportStat}
WHERE BORD_NO = #{bordNo}

UPDATE request110t SET
	STSF_RATE = #{starRating}
WHERE REQ_NO = #{reqNo}
AND   COMPANY_ID = #{sllrNo}
