mapper.select:
SELECT A.BORD_TYPE AS bordType , A.BORD_NO AS bordNo , A.DEL_YN AS delYn , (SELECT APT_NAME FROM apt000t WHERE APT_NO = A.BORD_NO ) bordName , A.BORD_TITLE AS bordTitle , A.BORD_SUB_TITLE AS bordSubTitle , A.BORD_CONTENT AS bordContent , A.BORD_RD_CNT AS bordRdCnt , A.BORD_GD_CNT AS bordGdCnt , (SELECT COUNT(S.CMMT_NO) FROM wit_bord110t S WHERE S.BORD_TYPE = A.BORD_TYPE AND S.BORD_NO = A.BORD_NO) AS commentCnt , (SELECT S.NICK_NAME FROM prsn000t S WHERE S.CLERK_NO = A.CRE_USER) AS creUser , A.CRE_DATE AS creDate , CASE WHEN TIMESTAMPDIFF(MINUTE, A.CRE_DATE, NOW()) < 5 THEN '방금 전' WHEN TIMESTAMPDIFF(HOUR, A.CRE_DATE, NOW()) < 1 THEN CONCAT(TIMESTAMPDIFF(MINUTE, A.CRE_DATE, NOW()), '분 전') WHEN TIMESTAMPDIFF(HOUR, A.CRE_DATE, NOW()) < 4 THEN CONCAT(TIMESTAMPDIFF(HOUR, A.CRE_DATE, NOW()), '시간 ', MOD(TIMESTAMPDIFF(MINUTE, A.CRE_DATE, NOW()), 60), '분 전') WHEN DATE(CRE_DATE) = CURDATE() THEN DATE_FORMAT(A.CRE_DATE, '%H:%i') ELSE DATE_FORMAT(A.CRE_DATE, '%y.%m.%d') END AS creDateTxt , A.UPD_USER AS updUser , A.UPD_DATE AS updDate , (SELECT CONCAT(S.FILE_LOC, S.FILE_ID) FROM wit_file000t S WHERE S.BIZ_CD = 'B001' AND S.BIZ_KEY = A.BORD_NO LIMIT 1) AS imagePath FROM wit_bord100t A WHERE A.DEL_YN = 'N' AND A.BORD_TYPE = #{bordType}
AND A.BORD_TITLE LIKE CONCAT('%', #{searchText}, '%')
ORDER BY A.CRE_DATE DESC LIMIT #{pageSize} OFFSET #{currentPage}

mapper.update:
UPDATE wit_bord100t SET
	BORD_RD_CNT = BORD_RD_CNT + 1
WHERE DEL_YN = 'N'
AND BORD_TYPE = #{bordType}
AND BORD_NO = #{bordNo}

UPDATE wit_bord100t SET
	  BORD_TITLE = #{bordTitle}
	, BORD_CONTENT = #{bordContent}
	, UPD_USER = #{updUser}
	, UPD_DATE = NOW()
WHERE BORD_TYPE = #{bordType}
AND BORD_NO = #{bordNo}

UPDATE wit_bord100t SET
	  DEL_YN = 'Y'
	, UPD_USER = #{updUser}
	, UPD_DATE = NOW()
WHERE DEL_YN = 'N'
AND BORD_TYPE = #{bordType}
AND BORD_NO = #{bordNo}

mapper.insert:
INSERT INTO wit_bord110t
(
	  CMMT_NO
	, CMMT_SEQ
	, BORD_TYPE
	, BORD_NO
	, DEL_YN
	, CMMT_CONTENT
	, CRE_USER
	, CRE_DATE
) 
VALUES
(
	(SELECT IFNULL(MAX(CAST(S.CMMT_NO AS UNSIGNED)), 0) + 1 FROM wit_bord110t S
		WHERE S.BORD_TYPE = #{bordType}
		AND S.BORD_NO = #{bordNo})
	, (SELECT IFNULL(MAX(CAST(S.CMMT_SEQ AS UNSIGNED)), 0) + 1 FROM wit_bord110t S
		WHERE S.BORD_TYPE = #{bordType}
		AND S.BORD_NO = #{bordNo})
	, #{bordType}
	, #{bordNo}
	, 'N'
	, #{cmmtContent}
	, #{creUser}
	, NOW()
)

mapper.update:
UPDATE wit_bord110t SET
	  DEL_YN = 'Y'
	, UPD_USER = #{creUser}
	, UPD_DATE = NOW()
WHERE DEL_YN = 'N'
AND BORD_TYPE = #{bordType}
AND BORD_NO = #{bordNo}

mapper.insert:
INSERT INTO wit_file000t
(
	  BIZ_CD
	, BIZ_KEY
	, FILE_SEQ
	, FILE_TYPE
	, FILE_ID
	, FILE_NM
	, FILE_LOC
	, FILE_SIZE
	, CRE_USER
	, CRE_DATE
) 
VALUES
(
	  'B001'
	, #{bizKey}
	, (SELECT IFNULL(MAX(CAST(S.FILE_SEQ AS UNSIGNED)), 0) + 1 FROM wit_file000t S
		WHERE S.BIZ_CD = 'B001'
		AND S.FILE_TYPE = #{fileType}
		AND S.BIZ_KEY = #{bizKey})
	, #{fileType}
	, #{fileId}
	, #{fileNm}
	, #{fileLoc}
	, #{fileSize}
	, #{creUser}
	, NOW()
)
