SELECT A.REQ_NO AS estNo , B.SEQ AS seq , C.APT_NAME AS aptName , D.IMAGEPATH AS itemImage , B.REQ_DATE estDt , B.REQ_CONTENTS reqContents , B.COMPANY_ID companyId , B.CATEGORY_ID categoryId , D.CATEGORY_NM AS itemName , B.REQ_STATE reqState , (SELECT CD_NM FROM wit_code000t WHERE CD_CLS = 'EST01' AND APPL_EN_DATE = '99991231235959' AND CD = B.REQ_STATE) AS stat , B.estimate_contents content , B.REQ_USER reqUser , IF(B.auto_yn = 'Y', '바로 견적서비스 발송', '일반 견적서비스 발송') AS autoYn , E.NICK_NAME AS prsnName , (SELECT category_nm FROM category WHERE category_id = B.category_id) AS categoryNm , B.ESTIMATE_AMOUNT itemPrice1 , DATE_FORMAT(B.ESTIMATE_DATE, '%Y%m%d') AS estimateDate , DATE_FORMAT(B.ESTIMATE_PROC_DATE, '%Y%m%d') AS estimateProcDate FROM request100t A, request110t B, apt000t C, category D, prsn000t E, wit_sllr000t F WHERE A.REQ_NO = B.REQ_NO AND B.END_DATE = '99991231' AND B.COMPANY_ID = #{sllrNo}
AND A.CATEGORY_ID = F.SERVICE_ITEM
AND B.COMPANY_ID = F.SLLR_NO
AND DATE_FORMAT(B.REQ_DATE, '%Y%m') = #{basDate}
AND A.REQ_GUBUN = 'G' AND C.APT_NO = #{aptNo}
AND B.REQ_STATE != 10
AND B.REQ_STATE = 10
AND C.APT_NO = A.APT_NO AND B.CATEGORY_ID = D.CATEGORY_ID AND B.REQ_USER = E.CLERK_NO ORDER BY B.REQ_DATE DESC

