mapper.select:
SELECT A.CTGR_ID AS ctgrId , A.END_DATE AS endDate , A.GP_START_DATE AS gpStartDate , A.GP_END_DATE AS gpEndDate , A.GP_STAT AS gpStat , (SELECT CD_NM FROM wit_code000t C WHERE C.CD_CLS = 'GP01' AND C.CD = A.GP_STAT AND C.APPL_EN_DATE = '99991231235959' ) AS gpStatName , A.LIMIT_COUNT AS limitCount , A.REQ_COUNT AS reqCount , A.SALE_RATE AS saleRate , A.SALE_AMT AS saleAmt , A.REG_USER AS regUser , A.REG_DATE AS regDate , A.UDT_USER AS udtDate , A.UDT_DATE AS udtDate , B.APT_NO AS aptNo , C.APT_NAME AS aptName FROM IBJ_CTGR100T A, IBJ_CTRG010T B, IBJ_APT000T C, IBJ_SLLR200T D WHERE A.CTGR_ID = B.CTGR_ID AND B.APT_NO = C.APT_NO AND B.APT_NO = D.APT_NO
AND A.CTGR_ID = #{ctgrId}
AND C.APT_NO = #{aptNo}
AND D.SLLR_NO = #{sllrNo}
AND A.END_DATE = '99991231' AND B.END_DATE = '99991231' AND C.END_DATE = '99991231' AND D.END_DATE = '99991231'

mapper.update:
UPDATE IBJ_CTGR100T
SET    GP_STAT = #{gpStat}
       , UDT_USER = #{udtUser}
       , UDT_DATE = SYSDATE()
WHERE  CTGR_ID = #{ctgrId}
